sources.sass/base/_mixins.scss:
@mixin theme-aware($key, $color) {
  @each $theme-name, $theme-color in $themes {
    .theme-#{$theme-name} & {
      #{$key}: map-get(map-get($themes, $theme-name), $color);
    }
  }
}

@mixin theme-background($imgpath) {
  background: {
    image: url($imgpath);
  }
}

